import React, { Component } from 'react';
import Input from '@material-ui/core/Input';
import PropTypes from 'prop-types';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';


const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing.unit * 2,
  },
});

class AddStateResult extends Component {

state = {
	stateresult:'',
	labelWidth: 0
	}




	handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {


    return (
    	 <form autoComplete="off">
    	 	<label>Select a State</label>
        	<FormControl>
        	<InputLabel htmlFor="age-simple">State</InputLabel>
	          <Select
	            value={this.state.stateresult}
	            onChange={this.handleChange}
	            inputProps={{
	              name: 'stateresult',
	              id: 'stateresult-simple'
	            }}
	          >

	          <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={'Abia'}>Abia State</MenuItem>
            <MenuItem value={'Adamawa'}>Adamawa State</MenuItem>
            <MenuItem value={'Akwa Ibom'}>Akwa Ibom</MenuItem>
            <MenuItem value={'Anambra'}>Anambra State</MenuItem>
            <MenuItem value={'Benue'}>Benue State</MenuItem>
            <MenuItem value={'Borno'}>Borno State</MenuItem>
          </Select>
        </FormControl>

        </form>
    )
    }
}


 export default AddStateResult;
