import React, { Component } from 'react';
import Input from '@material-ui/core/Input';
import PropTypes from 'prop-types';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';


class AddStateResult extends Component {

state = {
	stateresult:'',
	labelWidth: 0
	}




	handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {


    return (
    	<div>
    	{/*<label>Select a State</label>
    	 <form autoComplete="off">

        	<FormControl>
        	<InputLabel htmlFor="age-simple">State</InputLabel>
	          <Select
	            value={this.state.stateresult}
	            onChange={this.handleChange}
	            inputProps={{
	              name: 'stateresult',
	              id: 'stateresult-simple'
	            }}
	          >

	          <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={'Abia'}>Abia State</MenuItem>
            <MenuItem value={'Adamawa'}>Adamawa State</MenuItem>
            <MenuItem value={'Akwa Ibom'}>Akwa Ibom</MenuItem>
            <MenuItem value={'Anambra'}>Anambra State</MenuItem>
            <MenuItem value={'Benue'}>Benue State</MenuItem>
            <MenuItem value={'Borno'}>Borno State</MenuItem>
          </Select>
        </FormControl>

        </form>*/}

        <form onSubmit={this.props.handleSubmit}>
        	<br />
        	<h3>Add State</h3>
        		<label>Name of State: </label>
        		<select onChange={this.props.handleChange} name='nameofstate' value={this.props.nameofstate}>
					<option value=''>-- Please select a state --</option>
					<option value='Abia'>Abia State</option>
					<option value='Akwa Ibom'>Akwa Ibom State</option>
					<option value='Anambra'>Anambra State</option>
					<option value='Benue'>Benue State</option>
				</select>
				<br />
				<p><input type='text' placeholder='Please enter APC votes' name='apcVotes' onChange={this.props.handleChange} /></p>
				<p><input type='text' placeholder='Please enter PDP votes' name='pdpVotes' onChange={this.props.handleChange} /></p>
				<br />
				<button type="submit">Collate</button> 
        </form>

        </div>
    )
    }
}


 export default AddStateResult;
