import React, { Component } from 'react';
import NavBar from './components/Layouts/NavBar.jsx';
import AddStateResult from './components/Layouts/AddStateResult.jsx';
//import DisplayResultsResult from './components/Layouts/DisplayResults.jsx';

class App extends Component {

state = {
  nameofstate: '',
  apcVotes: '',
  pdpVotes: '',
	}

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value 
    })

  }

  handleSubmit = (e) => {
    e.preventDefault();

    const stateVote= {
      name: this.state.nameofstate,
      apcVotes: this.state.apcVotes,
      pdpVotes: this.state.pdpVotes
    }

    // this.setState({nameofstate: stateVote.name})
    console.log(stateVote.name)
    console.log(stateVote.apcVotes)

  }

  newEntry=<div>
    <br />
    <table>
    <caption>Collated Results</caption>
  <tr>
    <th>State</th>
    <th>APC</th>
    <th>PDP</th>
  </tr>
   <tr>
    <td>this.state.nameofstate</td>
    <td>this.state.apcVotes</td>
    <td>this.state.pdpVotes</td>
  </tr>
    </table>
    </div>

  render() {
    return (
      <div className="App">
        <NavBar />
        <AddStateResult 
          handleChange={this.handleChange} 
          nameofstate={this.state.nameofstate} 
          apcVotes={this.state.apcVotes}
          pdpVotes={this.state.pdpVotes}
          handleSubmit={this.handleSubmit}
          />

          {this.newEntry}

      </div>
    );
  }
}

export default App;
