import React, { Component } from 'react';
import NavBar from './components/Layouts/NavBar.jsx';
import AddStateResult from './components/Layouts/AddStateResult.jsx';

class App extends Component {

state = {
  nameofstate: '',
  apcVotes: '',
  pdpVotes: '',

  stateVote: {
      name: '',
      apcVotes: '',
      pdpVotes: ''
    }

	}

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value 
    })

  }

  handleSubmit = (e) => {
    e.preventDefault();
    const nameofstate=this.state.nameofstate
    const apcVotes=this.state.apcVotes
    const pdpVotes=this.state.pdpVotes

    this.setState({[this.stateVote.name]: nameofstate})
    console.log(this.state.stateVote.name)
  }

  render() {
    return (
      <div className="App">
        <NavBar />
        <AddStateResult 
          handleChange={this.handleChange} 
          nameofstate={this.state.nameofstate} 
          apcVotes={this.state.apcVotes}
          pdpVotes={this.state.pdpVotes}
          handleSubmit={this.handleSubmit}
          />
      </div>
    );
  }
}

export default App;
