import React, { Component } from 'react';
import NavBar from './components/Layouts/NavBar.jsx';
import AddStateResult from './components/Layouts/AddStateResult.jsx';

class App extends Component {

state = {
  nameofstate: '',
  apcVotes: '',
  pdpVotes: '',

	}

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value 
    })

  }

  handleSubmit = (e) => {
    e.preventDefault();

    const stateVote= {
      name: this.state.nameofstate,
      apcVotes: this.state.apcVotes,
      pdpVotes: this.state.pdpVotes
    }

    // this.setState({nameofstate: stateVote.name})
    console.log(this.stateVote.name)
  }

  render() {
    return (
      <div className="App">
        <NavBar />
        <AddStateResult 
          handleChange={this.handleChange} 
          nameofstate={this.state.nameofstate} 
          apcVotes={this.state.apcVotes}
          pdpVotes={this.state.pdpVotes}
          handleSubmit={this.handleSubmit}
          />
      </div>
    );
  }
}

export default App;
