import React, {Component} from 'react'
import PDPImage from '../../assets/PDP-logo.jpg'
import APCImage from '../../assets/APC.jpg'

class ViewResults extends Component{

	state={
		Infosource:[1,2,3,4,5,6,7,8,9]
	}

	getAPCTotal=()=>{
    this.state.Infosource.reduce((sum,a) => {
      sum+=a
      console.log(sum)
      return sum      
    }
    )
  }



render(){
	return(
		<div>
			<h2 className="m-3">2019 Nigeria General Elections</h2>
			<row className="m-3">
			<img src={APCImage} alt='APC' width='500' />
			<img src={PDPImage} alt='PDP' width='800'/>
			</row>
			<br />
			<row className="m-3">
				<label className="m-3">Total Votes:</label>
				<input type="text" name="APCtotal"/>
				<input type="text" name="PDPtotal" style={{marginLeft: '450px'}}/>
			</row>
		</div>
		)
	}
}

export default ViewResults;
